import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { api } from '../api';
import FileBrowser from './FileBrowser';
import CommitHistory from './CommitHistory';
import BranchManager from './BranchManager';
import PushPanel from './PushPanel';

const TABS = ['Code', 'Commits', 'Branches', 'Push'];

export default function RepoView() {
  const { repoName } = useParams();
  const navigate = useNavigate();
  const [repo, setRepo] = useState(null);
  const [tab, setTab] = useState('Code');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentBranch, setCurrentBranch] = useState('');

  const load = async () => {
    setLoading(true);
    try {
      const data = await api.getRepo(repoName);
      setRepo(data);
      setCurrentBranch(data.currentBranch || 'main');
    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, [repoName]);

  const handleCheckout = async (branch) => {
    try {
      await api.checkoutBranch(repoName, branch);
      setCurrentBranch(branch);
      load();
    } catch (e) {
      alert(e.message);
    }
  };

  if (loading) return <div className="loading-page"><span className="spinner" /> Loading…</div>;
  if (error) return (
    <div>
      <div className="error-banner">{error}</div>
      <Link to="/" className="btn">← Back to repositories</Link>
    </div>
  );

  return (
    <div>
      <div className="repo-view-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          <Link to="/" style={{ color: 'var(--text-secondary)', textDecoration: 'none', fontSize: 14 }}>Repositories</Link>
          <span style={{ color: 'var(--text-secondary)' }}>/</span>
          <span className="repo-view-title">{repo.name}</span>
        </div>
        {repo.description && <div className="repo-view-subtitle">{repo.description}</div>}
      </div>

      <div className="repo-tabs">
        {TABS.map(t => (
          <button key={t} className={`repo-tab ${tab === t ? 'active' : ''}`} onClick={() => setTab(t)}>{t}</button>
        ))}
      </div>

      {tab === 'Code' && (
        <>
          <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: 8 }}>
            <a
              className="btn btn-sm"
              href={api.getRepoDownloadUrl(repoName, currentBranch)}
              download
            >
              ↓ Download ZIP
            </a>
          </div>
          <FileBrowser
            repoName={repoName}
            currentBranch={currentBranch}
            branches={repo.branches || []}
            onCheckout={handleCheckout}
            onRefresh={load}
          />
        </>
      )}
      {tab === 'Commits' && (
        <CommitHistory repoName={repoName} currentBranch={currentBranch} />
      )}
      {tab === 'Branches' && (
        <BranchManager
          repoName={repoName}
          repo={repo}
          currentBranch={currentBranch}
          onCheckout={handleCheckout}
          onRefresh={load}
        />
      )}
      {tab === 'Push' && (
        <PushPanel
          repoName={repoName}
          currentBranch={currentBranch}
          branches={repo.branches || []}
          onRefresh={load}
        />
      )}
    </div>
  );
}
